import React from 'react';
import './section.scss';

interface SectionProps {
    image: string;
    text: string;
    flip: boolean;
    forumLink: string;
    videoLink: string;
}

const Section : React.FC<SectionProps> = ({image, text, flip}) => {
    return (
        <div className={`c-section ${flip ? 'c-section__reverse' : ''}`}>
            <div>
                <img src={image} alt="section__image" />
            </div>
            <div>
                <p>{text}</p>
            </div>
        </div>
    )    
}

export default Section;
